import { HttpClient, HttpErrorResponse, HttpHeaders, HttpParams } from '@angular/common/http';
import { Injectable, OnInit } from '@angular/core';
import { environment } from 'environments/environment';
import { throwError, Observable } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class DataanalyticsService {

  baseUrl = environment.baseUrl;

  constructor(private http: HttpClient) { }

  // make GET request by this
  getData(_URL: string): Observable<any> {
    const URL = this.baseUrl + _URL;
    return this.http.get(URL).pipe(catchError(this.handleError));
  }

  postData(_URL, body?): Observable<any> {
    const URL = this.baseUrl + _URL;
    const authToken = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..y8xOyoUfLHu645FoUpRhw_5I-x0_irXLOhHv2q7rsmM';
    const Theaders = new HttpHeaders({ 'Content-Type': 'application/json', Authorization: `Bearer ${authToken}` });
    return this.http.post<any>(URL, body)
      .pipe(catchError(this.handleError));
  }

  private handleError(error: HttpErrorResponse) {
    if (error.error instanceof ErrorEvent) {
      // A client-side or network error occurred. Handle it accordingly.
      console.error('An error occurred:', error.error.message);
    } else {
      // The backend returned an unsuccessful response code.
      // The response body may contain clues as to what went wrong.
      console.error(
        `Backend returned code ${error.status}, ` +
        `body was: ${error.error}`);
    }
    // Return an observable with a user-facing error message.
    return throwError(
      'Something bad happened; please try again later.');
  }
}
